import React, {useRef, useEffect, useState} from 'react'
import * as d3 from 'd3'

const initialBooks = [
    {
        name: "Harry Potter and the Philosophers Stone",
        author: "J. K. Rowling",
        genre: "fantasy"
    },{
        name: "The Pedagogy of Freedom",
        author: "Bell hooks",
        genre: "non-fiction"
    },{
        name: "Harry Potter and the Chamber of Secrets",
        author: "J. K. Rowling",
        genre: "fantasy"
    },{
        name: "Gilgamesh",
        author: "Derrek Hines",
        genre: "poetry"
    }
]

const Circle = () => {
    const ref  = useRef() // uses a ref to store a reference to our rendered <svg> element

    useEffect(() => {
        const svgElement = d3.select(ref.current)

        svgElement.append("circle")
            .attr("cx", 150)
            .attr("cy", 70)
            .attr("r", 50)
    }, [])
    return (
           <svg
            ref={ref}
           />

    )
}

export default Circle
