import React, {useRef, useEffect, useState} from 'react'
import * as d3 from 'd3'

let dataSet = await d3.csv("../utils/data.csv")
const Circle = () => {
    const ref  = useRef() // uses a ref to store a reference to our rendered <svg> element

    useEffect(() => { // runs d3 code when the Component mounths
        const svgElement = d3.select(ref.current) // turn our ref into d3 object

        svgElement.append("circle") //append a circle
            .attr("cx", 150)
            .attr("cy", 70)
            .attr("r", 50)
    }, [])
    return (
           <svg
            ref={ref}
           />

    )
}

export default Circle
